namespace Geissweb\Euvat\Plugin\Adminhtml\Form;

use Geissweb\Euvat\Helper\Configuration;
use Magento\Framework\View\Element\UiComponentInterface;

/**
 * Class CustomerAddress
 */
class CustomerAddress
{
    /**
     * @var Configuration
     */
    public $configHelper;

    /**
     * CheckoutLayout constructor.
     *
     * @param Configuration $config
     */
    public function __construct(
        Configuration $config
    ) {
        $this->configHelper = $config;
    }

    /**
     * Adds the vat_id field JS configuration
     *
     * @param                      $subject
     * @param UiComponentInterface $component
     *
     * @throws \Magento\Framework\Exception\NoSuchEntityException
     */
    public function beforeRegister($subject, UiComponentInterface $component)
    {
        // Appears only on the address form
        if ($component->getName() === 'vat_id') {
            $baseConfig = $component->getData('config');
            $additionalConfig = $this->configHelper->getAdminVatFieldConfig();
            $finalConfig = array_merge($baseConfig, $additionalConfig);
            $component->setData('config', $finalConfig);
        }
    }
}
